INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(2, 1043, 'Tuấn Trần', 'admin@thietkewebtgroup.com', 'http://thietkewebtgroup.com', '27.74.156.195', '2016-07-30 14:57:45', '2016-07-30 14:57:45', 'Dạ nhà trường cho em hỏi là hiện em đã có bằng đại học chính quy CNTT, nếu em đăng ký theo học ngành Quản trị kinh doanh thì có được chuyển điểm một số môn qua không ạ? Em xin chân thành cảm ơn.', 0, '1', '', '', 0, 0),
(3, 1043, 'Quản Trị', 'ngocthanh27@gmail.com', 'http://enre.edu.vn/', '14.177.152.211', '2016-08-02 00:51:32', '2016-08-02 00:51:32', 'Kính gửi Tuấn.\nTrong bảng điểm mà e học ở VB 1 có môn nào trùng với chương trình đào tạo của ngành QTKD thì e được chuyển điểm.\nTrân trọng cảm ơn.', 0, '1', '', '', 2, 2);
